[color=#2b91af]int[/color] [color=#06287e]main[/color]([color=#2b91af]int[/color] argc, [color=#2b91af]char[/color] [color=#666666]**[/color]argv)
{
        LCUI_Widget root, pack;

        LCUI_Init();
        LCDesgin_Init();
        root [color=#666666]=[/color] LCUIWidget_GetRoot();
        pack [color=#666666]=[/color] LCUIBuilder_LoadFile([color=#a31515]"main.xml"[/color]);
        [color=#0000ff][b]if[/color][/b]([color=#666666]![/color]pack) {
                [color=#0000ff][b]return[/color][/b] [color=#666666]-[/color][color=#40a070]1[/color];
        }
        Widget_Append(root, pack);
        Widget_Unwrap(pack);
        [color=#0000ff][b]return[/color][/b] LCUI_Main();
}
